// Обработка и валидация запроса
const query = $input.first().json.query || $input.first().json.body?.query;
const options = $input.first().json.options || $input.first().json.body?.options || {};

// Валидация
if (!query || query.trim().length === 0) {
  throw new Error('Query is required for RAG search');
}

// Подготовка параметров поиска
const searchOptions = {
  limit: options.limit || 10,
  threshold: options.threshold || 0.7,
  category: options.category || null,
  tags: options.tags || [],
  date_from: options.date_from || null,
  date_to: options.date_to || null
};

// Подготовка для анализа запроса
const analysisData = {
  original_query: query.trim(),
  search_options: searchOptions,
  timestamp: new Date().toISOString()
};

return {
  json: analysisData
};

// Обработка анализа запроса и подготовка поисковых параметров
const analysisResponse = $input.first().json.response;
let queryAnalysis = {};

try {
  const jsonMatch = analysisResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    queryAnalysis = JSON.parse(jsonMatch[0]);
  } else {
    // Fallback анализ
    queryAnalysis = {
      search_terms: [$('Query Processor').first().json.original_query],
      intent: 'general_search',
      context: 'general',
      suggested_categories: [],
      suggested_tags: [],
      search_strategy: 'text_match'
    };
  }
} catch (error) {
  queryAnalysis = {
    search_terms: [$('Query Processor').first().json.original_query],
    intent: 'general_search',
    context: 'general',
    suggested_categories: [],
    suggested_tags: [],
    search_strategy: 'text_match'
  };
}

// Построение Supabase запроса
const searchTerms = queryAnalysis.search_terms.join(' ');
const categories = queryAnalysis.suggested_categories;
const tags = queryAnalysis.suggested_tags;

// Создание фильтров для Supabase
let supabaseQuery = `select=*`;
let supabaseFilters = [];

// Фильтр по категории
if (categories.length > 0) {
  supabaseFilters.push(`or=(${categories.map(cat => `analysis->category.ilike.*${cat}*`).join(',')})`);
}

// Фильтр по тегам
if (tags.length > 0) {
  supabaseFilters.push(`or=(${tags.map(tag => `analysis->tags.cs.{${tag}}`).join(',')})`);
}

// Фильтр по тексту (full-text search)
supabaseFilters.push(`or=(original_text.ilike.*${searchTerms}*,processed_text.ilike.*${searchTerms}*,analysis->summary.ilike.*${searchTerms}*)`);

// Лимит результатов
const limit = $('Query Processor').first().json.search_options.limit;
supabaseQuery += `&limit=${limit}`;

// Сортировка по дате
supabaseQuery += `&order=created_at.desc`;

// Добавление фильтров
if (supabaseFilters.length > 0) {
  supabaseQuery += `&${supabaseFilters.join('&')}`;
}

return {
  json: {
    original_query: $('Query Processor').first().json.original_query,
    query_analysis: queryAnalysis,
    search_options: $('Query Processor').first().json.search_options,
    supabase_query: supabaseQuery,
    search_terms: searchTerms
  }
};

// Обработка результатов поиска и ранжирование
const searchResults = $input.first().json;
const queryAnalysis = $('Search Query Builder').first().json.query_analysis;
const originalQuery = $('Search Query Builder').first().json.original_query;

// Проверка наличия результатов
if (!searchResults || searchResults.length === 0) {
  return {
    json: {
      query: originalQuery,
      results: [],
      total_found: 0,
      message: 'No relevant knowledge found for your query',
      suggestions: [
        'Try different keywords',
        'Check spelling',
        'Use broader terms',
        'Try related categories'
      ]
    }
  };
}

// Простое ранжирование результатов
const rankedResults = searchResults.map((result, index) => {
  let score = 0;

  // Базовый score по позиции
  score += (searchResults.length - index) * 0.1;

  // Проверка совпадения в заголовке/суммарии
  const summary = result.analysis?.summary || '';
  const originalText = result.original_text || '';
  const processedText = result.processed_text || '';

  if (summary.toLowerCase().includes(originalQuery.toLowerCase())) {
    score += 0.3;
  }

  if (originalText.toLowerCase().includes(originalQuery.toLowerCase())) {
    score += 0.2;
  }

  if (processedText.toLowerCase().includes(originalQuery.toLowerCase())) {
    score += 0.1;
  }

  // Проверка тегов
  const tags = result.analysis?.tags || [];
  const queryTerms = queryAnalysis.search_terms || [];

  queryTerms.forEach(term => {
    if (tags.some(tag => tag.toLowerCase().includes(term.toLowerCase()))) {
      score += 0.1;
    }
  });

  // Проверка категории
  if (queryAnalysis.suggested_categories?.includes(result.analysis?.category)) {
    score += 0.2;
  }

  return {
    ...result,
    relevance_score: Math.min(score, 1.0),
    rank: index + 1
  };
});

// Сортировка по relevance score
rankedResults.sort((a, b) => b.relevance_score - a.relevance_score);

// Подготовка финального ответа
const finalResults = rankedResults.map(result => ({
  id: result.id,
  summary: result.analysis?.summary || 'No summary available',
  original_text: result.original_text?.substring(0, 500) + (result.original_text?.length > 500 ? '...' : ''),
  category: result.analysis?.category || 'general',
  tags: result.analysis?.tags || [],
  key_concepts: result.analysis?.key_concepts || [],
  metadata: result.metadata,
  created_at: result.created_at,
  relevance_score: result.relevance_score
}));

return {
  json: {
    query: originalQuery,
    query_analysis: queryAnalysis,
    results: finalResults,
    total_found: finalResults.length,
    search_strategy: queryAnalysis.search_strategy,
    timestamp: new Date().toISOString()
  }
};
